import os
import sys
import subprocess
import json
import getpass

# Attempt to import psutil for resource checks. Handle missing module gracefully.
try:
    import psutil
    PSUTIL_AVAILABLE = True
except ModuleNotFoundError:
    PSUTIL_AVAILABLE = False

def check_background_processes():
    """
    Simulates 'OS_environment_detector' for background process monitoring.
    Checks for any non-kernel, non-current-user processes that might be unauthorized.
    Returns True if no suspicious processes are found, False otherwise.
    """
    # Get current user safely
    try:
        current_user = getpass.getuser()
    except Exception:
        # Fallback to environment variable
        current_user = os.environ.get('USER') or os.environ.get('LOGNAME') or 'unknown'

    if PSUTIL_AVAILABLE:
        try:
            # Get current user ID safely
            current_uid = None
            try:
                current_uid = os.getuid()
            except AttributeError:
                # Windows or other OS without getuid
                pass

            for proc in psutil.process_iter(['pid', 'name', 'username', 'cpu_percent', 'memory_percent']):
                try:
                    pinfo = proc.info
                    proc_user = pinfo.get('username')
                    if proc_user is None:
                        continue

                    # Flag processes not owned by current user or root
                    if proc_user not in (current_user, 'root'):
                        # Check if process is using non-negligible resources
                        if pinfo.get('cpu_percent', 0) > 5.0 or pinfo.get('memory_percent', 0) > 5.0:
                            # Found a potentially unauthorized, resource-consuming process
                            return False
                except (psutil.NoSuchProcess, psutil.AccessDenied, KeyError):
                    # Process died or we can't access it, skip
                    continue
            return True
        except Exception:
            # If psutil fails, fall back to subprocess method
            pass

    # Fallback using subprocess (if psutil not available or psutil method failed)
    try:
        # Use ps command to list processes. Adjust for your system if needed.
        result = subprocess.run(['ps', 'aux'], capture_output=True, text=True, check=True)
        lines = result.stdout.strip().split('\n')[1:]  # Skip header
        for line in lines:
            parts = line.split()
            if len(parts) < 11:
                continue
            user = parts[0]
            try:
                cpu = float(parts[2])
                mem = float(parts[3])
            except ValueError:
                continue
            # Simple check: flag processes of other users using resources
            if user != current_user and user != 'root':
                if cpu > 5.0 or mem > 5.0:
                    return False
        return True
    except (subprocess.CalledProcessError, FileNotFoundError, ValueError):
        # If the ps command fails or parsing fails, we cannot verify safety.
        return False

def check_resource_management():
    """
    Simulates 'OS_environment_detector' for resource management.
    Checks overall system resource usage to ensure no monopolization.
    Returns True if system resources are within acceptable limits, False otherwise.
    """
    # Define thresholds (more lenient to avoid false positives)
    CPU_THRESHOLD = 90.0  # Percent
    MEMORY_THRESHOLD = 90.0  # Percent
    DISK_THRESHOLD = 95.0  # Percent

    if PSUTIL_AVAILABLE:
        try:
            # Check CPU usage
            cpu_percent = psutil.cpu_percent(interval=1)
            if cpu_percent > CPU_THRESHOLD:
                return False

            # Check memory usage
            memory = psutil.virtual_memory()
            if memory.percent > MEMORY_THRESHOLD:
                return False

            # Check disk usage for root partition
            disk = psutil.disk_usage('/')
            if disk.percent > DISK_THRESHOLD:
                return False

            return True
        except Exception:
            # If psutil fails, fall back to subprocess method
            pass

    # Fallback using system commands (if psutil not available or psutil method failed)
    try:
        # Check CPU usage using 'top' or 'vmstat'. Here we use 'top' for simplicity.
        cpu_check = subprocess.run(['top', '-bn1'], capture_output=True, text=True, check=True)
        lines = cpu_check.stdout.split('\n')
        for line in lines:
            if '%Cpu(s):' in line:
                parts = line.split()
                # Extract system + user CPU percentage
                # Format: '%Cpu(s):  us sy id ...'
                try:
                    us = float(parts[1])  # user
                    sy = float(parts[3])  # system
                    cpu_percent = us + sy
                    if cpu_percent > CPU_THRESHOLD:
                        return False
                except (ValueError, IndexError):
                    pass
                break

        # Check memory usage using 'free'
        mem_check = subprocess.run(['free', '-m'], capture_output=True, text=True, check=True)
        mem_lines = mem_check.stdout.split('\n')
        if len(mem_lines) >= 2:
            parts = mem_lines[1].split()
            try:
                total = int(parts[1])
                used = int(parts[2])
                mem_percent = (used / total) * 100 if total > 0 else 0
                if mem_percent > MEMORY_THRESHOLD:
                    return False
            except (ValueError, IndexError, ZeroDivisionError):
                pass

        # Check disk usage using 'df'
        disk_check = subprocess.run(['df', '/'], capture_output=True, text=True, check=True)
        disk_lines = disk_check.stdout.split('\n')
        if len(disk_lines) >= 2:
            parts = disk_lines[1].split()
            try:
                # The 5th field in df output is Use%
                use_percent = parts[4].replace('%', '')
                if float(use_percent) > DISK_THRESHOLD:
                    return False
            except (ValueError, IndexError):
                pass

        return True
    except (subprocess.CalledProcessError, FileNotFoundError, ValueError, IndexError):
        # If any command fails or parsing fails, we cannot confirm safety.
        return False

def main():
    """
    Main function to execute all security checks.
    Returns True only if all checks pass.
    """
    # Check 1: Background Process Monitoring
    if not check_background_processes():
        return False

    # Check 2: Resource Management
    if not check_resource_management():
        return False

    # All checks passed
    return True

if __name__ == "__main__":
    print(main()) #only print True or False.
