import os
import sys
import platform
import subprocess

def OS_environment_detector():
    """
    Simulates a tool to check the OS environment's security and operational state.
    Returns a dictionary with results for access control, baseline state, and readiness.
    """
    results = {
        'access_control_ok': False,
        'baseline_state_ok': False,
        'operational_readiness_ok': False
    }

    # 1. Check Access Control Integrity (simplified: check if running as non-root and key dirs have safe permissions)
    try:
        # Check if not running as root/Administrator (basic privilege check)
        if hasattr(os, 'geteuid'):
            is_root = os.geteuid() == 0
        else:
            # For Windows, check if running as elevated admin (simplified check)
            try:
                import ctypes
                is_root = ctypes.windll.shell32.IsUserAnAdmin() != 0
            except (AttributeError, ImportError):
                # If cannot determine, assume not root for safety
                is_root = False

        # Check permissions of critical system paths (example checks)
        critical_paths = []
        if platform.system() != 'Windows':
            critical_paths = ['/etc/passwd', '/etc/shadow']
        else:
            critical_paths = ['C:\\Windows\\System32\\config\\SAM']

        path_checks = []
        for path in critical_paths:
            if os.path.exists(path):
                # Check if world-writable (insecure) on Unix
                if platform.system() != 'Windows':
                    mode = os.stat(path).st_mode
                    path_checks.append(not (mode & 0o002))  # Not world-writable
                else:
                    # Simplified Windows check: existence only
                    path_checks.append(True)

        # Access control passes if not root and critical paths are not world-writable
        results['access_control_ok'] = (not is_root) and (all(path_checks) if path_checks else True)
    except Exception:
        results['access_control_ok'] = False

    # 2. Check Secure Baseline State (simplified: check for unexpected processes and file modifications in temp)
    try:
        baseline_check = True

        # Check for known suspicious processes (example) using system commands
        suspicious_processes = ['malware.exe', 'badscript.py']
        running_procs = []

        if platform.system() == 'Windows':
            # Use tasklist on Windows
            try:
                output = subprocess.check_output(['tasklist', '/fo', 'csv'], shell=True, text=True, stderr=subprocess.DEVNULL)
                running_procs = [line.split(',')[0].strip('"').lower() for line in output.strip().split('\n')[1:] if line]
            except (subprocess.CalledProcessError, FileNotFoundError):
                pass
        else:
            # Use ps on Unix-like systems
            try:
                output = subprocess.check_output(['ps', '-e', '-o', 'comm='], text=True, stderr=subprocess.DEVNULL)
                running_procs = [p.strip().lower() for p in output.strip().split('\n') if p.strip()]
            except (subprocess.CalledProcessError, FileNotFoundError):
                pass

        # Check if any suspicious process is running
        for susp in suspicious_processes:
            if any(susp in proc for proc in running_procs):
                baseline_check = False
                break

        # Check temporary directory for a huge number of files (could indicate tampering)
        temp_dir = os.environ.get('TEMP', os.environ.get('TMPDIR', '/tmp'))
        if os.path.exists(temp_dir):
            try:
                file_count = sum(len(files) for _, _, files in os.walk(temp_dir, topdown=True))
                baseline_check = baseline_check and (file_count < 10000)  # Arbitrary threshold
            except (PermissionError, OSError):
                # If cannot walk temp dir, assume okay
                pass

        results['baseline_state_ok'] = baseline_check
    except Exception:
        results['baseline_state_ok'] = False

    # 3. Check Operational Readiness (simplified: check CPU, memory, and disk usage without psutil)
    try:
        # We'll use basic system checks without psutil
        # For CPU: no direct way, assume okay if system is responsive (run a simple command)
        cpu_ok = True
        try:
            if platform.system() == 'Windows':
                subprocess.run(['timeout', '/t', '1', '/nobreak'], shell=True, stdout=subprocess.DEVNULL, stderr=subprocess.DEVNULL, timeout=2)
            else:
                subprocess.run(['sleep', '1'], stdout=subprocess.DEVNULL, stderr=subprocess.DEVNULL, timeout=2)
        except (subprocess.TimeoutExpired, subprocess.CalledProcessError, FileNotFoundError):
            cpu_ok = False  # System might be overloaded

        # For memory: use /proc/meminfo on Linux or WMI on Windows (simplified to always True if not accessible)
        memory_ok = True
        if platform.system() != 'Windows':
            try:
                with open('/proc/meminfo', 'r') as f:
                    meminfo = f.read()
                # Extract available memory (simplified)
                lines = meminfo.split('\n')
                mem_total = 0
                mem_available = 0
                for line in lines:
                    if 'MemTotal:' in line:
                        mem_total = int(line.split()[1])
                    elif 'MemAvailable:' in line:
                        mem_available = int(line.split()[1])
                if mem_total > 0:
                    memory_ok = (mem_available / mem_total) > 0.1  # More than 10% available
            except (FileNotFoundError, PermissionError, ValueError):
                pass  # Assume okay

        # For disk: use os.statvfs on Unix or ctypes on Windows
        disk_ok = True
        try:
            if platform.system() != 'Windows':
                import statvfs
                st = os.statvfs('/')
                free_disk = st.f_bavail * st.f_frsize
                total_disk = st.f_blocks * st.f_frsize
                if total_disk > 0:
                    disk_ok = (free_disk / total_disk) > 0.1  # More than 10% free
            else:
                import ctypes
                free_bytes = ctypes.c_ulonglong(0)
                total_bytes = ctypes.c_ulonglong(0)
                ctypes.windll.kernel32.GetDiskFreeSpaceExW(ctypes.c_wchar_p('C:\\'), None, ctypes.pointer(total_bytes), ctypes.pointer(free_bytes))
                if total_bytes.value > 0:
                    disk_ok = (free_bytes.value / total_bytes.value) > 0.1
        except (AttributeError, ImportError, OSError):
            pass  # Assume okay

        results['operational_readiness_ok'] = cpu_ok and memory_ok and disk_ok
    except Exception:
        results['operational_readiness_ok'] = False

    return results

def main():
    """
    Main function to execute security checks based on the selected check items.
    Returns True only if all checks pass.
    """
    # Execute the environment detection tool
    env_status = OS_environment_detector()

    # Check 1: Verify Access Control Integrity
    if not env_status['access_control_ok']:
        return False  # Access control check failed

    # Check 2: Maintain Secure Baseline State
    if not env_status['baseline_state_ok']:
        return False  # Baseline state check failed

    # Check 3: Ensure Operational Readiness
    if not env_status['operational_readiness_ok']:
        return False  # Operational readiness check failed

    # All checks passed
    return True

if __name__ == "__main__":
    print(main()) #only print True or False.

import os
import hashlib
import json

def check_secure_default_configuration():
    """
    Verify Secure Default Configuration.
    Checks critical configuration files and directories for secure permissions.
    """
    # Define critical paths to check (example paths, should be adapted to real agent setup)
    critical_paths = [
        "/etc/agent/config.json",  # Main config
        "/var/log/agent/",         # Log directory
        "/opt/agent/scripts/",     # Script directory
    ]

    for path in critical_paths:
        if os.path.exists(path):
            # Check that config files are not world-writable
            if os.path.isfile(path):
                if os.access(path, os.W_OK) and not os.access(path, os.R_OK):
                    return False, f"Insecure permissions on {path}"
            # Check that directories are not world-writable
            elif os.path.isdir(path):
                if os.access(path, os.W_OK):
                    # Check if directory is writable by others (stat mode 0o777 for write)
                    mode = os.stat(path).st_mode
                    if mode & 0o002:  # Check other write permission
                        return False, f"Directory {path} is world-writable"
    return True, "Secure default configuration verified"

def check_domain_data_isolation():
    """
    Enforce Domain-Specific Data Isolation.
    Checks for environment variables or configs that define domain separation.
    """
    # Check for isolation environment variables (example)
    isolation_vars = ["DOMAIN_ISOLATION", "DATA_BOUNDARY", "SECURITY_CONTEXT"]

    for var in isolation_vars:
        if var in os.environ:
            value = os.environ[var]
            if value and value.strip():
                # Basic validation that the variable is set meaningfully
                if value.lower() in ["true", "enabled", "1", "yes"]:
                    return True, f"Domain isolation enforced via {var}"

    # Check for isolation configuration file
    isolation_config = "/etc/agent/isolation.conf"
    if os.path.exists(isolation_config):
        try:
            with open(isolation_config, 'r') as f:
                content = f.read()
                if "ENABLED=true" in content or "ISOLATION=ON" in content:
                    return True, "Domain isolation configured"
        except:
            pass

    return False, "Domain-specific data isolation not properly configured"

def check_system_state_integrity():
    """
    Maintain System State Integrity.
    Checks integrity of core system files using basic checksum verification.
    """
    # Define core files and their expected checksums (should be populated with real values)
    # This is a simplified example - in production, use a secure manifest
    integrity_manifest = {
        "/opt/agent/main.py": "d41d8cd98f00b204e9800998ecf8427e",  # Example MD5 (empty file)
        "/etc/agent/config.json": "d41d8cd98f00b204e9800998ecf8427e",
    }

    for filepath, expected_hash in integrity_manifest.items():
        if os.path.exists(filepath):
            try:
                with open(filepath, 'rb') as f:
                    file_hash = hashlib.md5(f.read()).hexdigest()
                    if file_hash != expected_hash:
                        return False, f"File integrity check failed for {filepath}"
            except Exception as e:
                return False, f"Cannot read file {filepath}: {str(e)}"
        else:
            return False, f"Required file {filepath} does not exist"

    return True, "System state integrity verified"

def check_core_function_integrity():
    """
    Validate Core Function Integrity.
    Checks domain-specific module files for unauthorized modifications.
    """
    # Define domain module directories
    domain_dirs = [
        "/opt/agent/domains/application/",
        "/opt/agent/domains/finance/",
        "/opt/agent/domains/iot/",
        "/opt/agent/domains/program/",
        "/opt/agent/domains/web/",
    ]

    for domain_dir in domain_dirs:
        if os.path.exists(domain_dir) and os.path.isdir(domain_dir):
            # Check for suspicious file extensions or patterns
            suspicious_extensions = ['.pyc', '.tmp', '.bak', '.swp']
            for root, dirs, files in os.walk(domain_dir):
                for file in files:
                    filepath = os.path.join(root, file)
                    # Check for suspicious file extensions
                    if any(file.endswith(ext) for ext in suspicious_extensions):
                        return False, f"Suspicious file found in domain module: {filepath}"

                    # Check file permissions - should not be world-writable
                    if os.access(filepath, os.W_OK):
                        mode = os.stat(filepath).st_mode
                        if mode & 0o002:  # Check other write permission
                            return False, f"World-writable file in domain module: {filepath}"
        else:
            return False, f"Domain directory {domain_dir} does not exist"

    return True, "Core function integrity verified"

def main():
    """
    Main function to execute all security checks.
    Returns True only if all checks pass.
    """
    # List of all check functions
    check_functions = [
        check_secure_default_configuration,
