import os
import sys
import hashlib
import shutil

def check_file_permissions(path):
    """Check if file/directory has restrictive permissions (readable only by owner)."""
    if not os.path.exists(path):
        return True  # If path doesn't exist, consider it safe (no permission issue)
    stat = os.stat(path)
    # Check if group and others have no read/write/execute permissions
    return (stat.st_mode & 0o077) == 0

def check_log_safety(log_path):
    """Check if log file exists and does not contain obvious sensitive patterns."""
    sensitive_keywords = ["password", "secret", "key", "token", "credit_card", "ssn"]
    try:
        if os.path.exists(log_path):
            with open(log_path, 'r', encoding='utf-8', errors='ignore') as f:
                content = f.read(5000).lower()  # Sample first 5000 chars
                for keyword in sensitive_keywords:
                    if keyword in content:
                        return False
        return True
    except Exception:
        return False

def check_file_integrity(file_path, expected_hash=None):
    """Check if file exists and optionally verify its hash."""
    if not os.path.exists(file_path):
        return False
    if expected_hash:
        with open(file_path, 'rb') as f:
            file_hash = hashlib.sha256(f.read()).hexdigest()
            return file_hash == expected_hash
    return True

def check_system_resources():
    """Check if system resources are within safe limits."""
    try:
        # Try to use psutil if available
        import psutil
        # Memory usage below 90%
        if psutil.virtual_memory().percent > 90:
            return False
        # CPU usage below 80% (average over 1 second)
        if psutil.cpu_percent(interval=1) > 80:
            return False
    except ImportError:
        # If psutil is not installed, skip memory and CPU checks
        # Print a warning to stderr
        sys.stderr.write("Warning: psutil not installed, skipping memory and CPU checks.\n")
        pass

    # Disk usage below 95%
    try:
        disk_usage = shutil.disk_usage('/')
        disk_percent = (disk_usage.used / disk_usage.total) * 100
        if disk_percent > 95:
            return False
    except Exception:
        # If disk check fails, assume it's okay to not block the check
        sys.stderr.write("Warning: Disk usage check failed.\n")
        pass

    return True

def main():
    # Define critical paths (example paths - should be configured per deployment)
    # These are example paths; in production, they should be set via configuration
    sensitive_dirs = ["/etc/agent/config", "/var/lib/agent/data"]
    codebase_paths = ["/opt/agent", "/usr/local/agent"]
    log_paths = ["/var/log/agent.log", "/var/log/syslog"]
    cache_paths = ["/tmp/agent_cache", "/var/cache/agent"]

    # Check 1: Sensitive data stores have restrictive permissions
    for path in sensitive_dirs:
        if not check_file_permissions(path):
            return False

    # Check 2: Log files do not leak sensitive info
    for log_path in log_paths:
        if not check_log_safety(log_path):
            return False

    # Check 3: Codebase is protected (not writable by others)
    for code_path in codebase_paths:
        if os.path.exists(code_path):
            # Check directory and all .py files within
            for root, dirs, files in os.walk(code_path):
                for file in files:
                    if file.endswith('.py'):
                        full_path = os.path.join(root, file)
                        if os.access(full_path, os.W_OK) and not check_file_permissions(full_path):
                            return False

    # Check 4: Cache integrity (simplified - check if cache dir exists and is accessible)
    for cache_path in cache_paths:
        if os.path.exists(cache_path):
            # Try to read/write a test file to verify not corrupted
            test_file = os.path.join(cache_path, ".integrity_test")
            try:
                with open(test_file, 'w') as f:
                    f.write("test")
                with open(test_file, 'r') as f:
                    if f.read() != "test":
                        return False
                os.remove(test_file)
            except IOError:
                return False
            except Exception:
                # If any other error, consider it a failure
                return False

    # Check 5: System resources are stable
    if not check_system_resources():
        return False

    return True

if __name__ == "__main__":
    print(main())  # only print True or False
