import os
import sys
import subprocess

def check_confidentiality_defaults():
    """
    Verify that system default access controls and authentication mechanisms are active.
    """
    # Check if SSH daemon is running as a proxy for access controls.
    # Use pgrep to check for sshd process.
    try:
        # Try using pgrep (common on Unix)
        result = subprocess.run(['pgrep', 'sshd'], capture_output=True, text=True)
        if result.returncode == 0 and result.stdout.strip():
            return True
    except (subprocess.SubprocessError, FileNotFoundError):
        pass

    # Fallback: check via ps command
    try:
        result = subprocess.run(['ps', 'aux'], capture_output=True, text=True)
        if 'sshd' in result.stdout:
            return True
    except (subprocess.SubprocessError, FileNotFoundError):
        pass

    # If we cannot verify, assume insecure (fail-safe)
    return False

def check_integrity_defaults():
    """
    Verify that system integrity monitoring and protective measures are active.
    """
    # Check 1: Critical system directories are not world-writable.
    critical_paths = ['/etc', '/bin', '/usr/bin', '/sbin', '/usr/sbin']
    for path in critical_paths:
        if os.path.exists(path):
            if os.stat(path).st_mode & 0o002:  # world-writable
                return False

    # Check 2: Verify cron is running (for integrity scripts).
    try:
        result = subprocess.run(['pgrep', 'cron'], capture_output=True, text=True)
        if result.returncode == 0 and result.stdout.strip():
            return True
    except (subprocess.SubprocessError, FileNotFoundError):
        pass

    # Fallback: check via ps
    try:
        result = subprocess.run(['ps', 'aux'], capture_output=True, text=True)
        if 'cron' in result.stdout:
            return True
    except (subprocess.SubprocessError, FileNotFoundError):
        pass

    # If we cannot verify cron, assume integrity mechanisms might be impaired.
    return False

def check_availability_defaults():
    """
    Verify that system resources and services are in a stable, operational state.
    """
    # Check 1: System load average (using os.getloadavg)
    try:
        load1, load5, load15 = os.getloadavg()
        cpu_count = os.cpu_count()
        if cpu_count and load1 > cpu_count * 2:
            return False
    except OSError:
        # If getloadavg fails, we cannot check load.
        pass

    # Check 2: Available memory (using /proc/meminfo on Linux)
    try:
        with open('/proc/meminfo', 'r') as f:
            meminfo = f.read()
        # Parse MemAvailable (preferred) or calculate from MemFree
        lines = meminfo.split('\n')
        mem_available = None
        mem_total = None
        for line in lines:
            if line.startswith('MemAvailable:'):
                mem_available = int(line.split()[1])  # in kB
            elif line.startswith('MemTotal:'):
                mem_total = int(line.split()[1])
        if mem_available is not None and mem_total is not None:
            if mem_available < (mem_total * 0.05):  # Less than 5% available
                return False
        else:
            # Fallback: if MemAvailable not present, use MemFree
            mem_free = None
            for line in lines:
                if line.startswith('MemFree:'):
                    mem_free = int(line.split()[1])
            if mem_free is not None and mem_total is not None:
                if mem_free < (mem_total * 0.05):
                    return False
    except (FileNotFoundError, PermissionError, ValueError):
        # If /proc/meminfo not available, try using 'free' command
        try:
            result = subprocess.run(['free', '-k'], capture_output=True, text=True)
            lines = result.stdout.split('\n')
            if len(lines) >= 2:
                parts = lines[1].split()
                if len(parts) >= 4:
                    mem_total = int(parts[1])
                    mem_available = int(parts[3])  # available column in free -k
                    if mem_available < (mem_total * 0.05):
                        return False
        except (subprocess.SubprocessError, FileNotFoundError, ValueError):
            # If we cannot check memory, assume it's okay? But for safety, we fail.
            return False

    # Check 3: Root disk free space (using os.statvfs)
    try:
        stat = os.statvfs('/')
        total_blocks = stat.f_frsize * stat.f_blocks
        free_blocks = stat.f_frsize * stat.f_bavail
        if free_blocks < (total_blocks * 0.1):  # Less than 10% free
            return False
    except OSError:
        # If statvfs fails, try using 'df' command
        try:
            result = subprocess.run(['df', '/'], capture_output=True, text=True)
            lines = result.stdout.split('\n')
            if len(lines) >= 2:
                parts = lines[1].split()
                if len(parts) >= 5:
                    use_percent = parts[4].rstrip('%')
                    if float(use_percent) > 90:  # More than 90% used
                        return False
        except (subprocess.SubprocessError, FileNotFoundError, ValueError):
            # If we cannot check disk, assume it's okay? But for safety, we fail.
            return False

    return True

def main():
    """
    Main function to execute all security checks.
    Returns True only if all checks pass.
    """
    # Annotation: Starting security checks for idle agent state.

    # Check 1: Information Confidentiality - Default Access Controls
    if not check_confidentiality_defaults():
        # Annotation: Confidentiality check failed. Default access controls may not be active.
        return False

    # Check 2: Information Integrity - Default Integrity Measures
    if not check_integrity_defaults():
        # Annotation: Integrity check failed. System integrity protections may not be active.
        return False

    # Check 3: Information Availability - Default System State
    if not check_availability_defaults():
        # Annotation: Availability check failed. System resources may be unstable or insufficient.
        return False

    # Annotation: All security checks passed.
    return True

if __name__ == "__main__":
    print(main()) #only print True or False.
