System security risk.tool_code:
def root_ownership_protector(command: str):
    cmd = command.strip().lower()
    high_risk_patterns = [
        r'\bchown\b[^;&|]\s+-r[^;&|]\s+/\s$',
        r'\bchown\b[^;&|]\s+/\s$',
        r'\bchmod\b[^;&|]\s+-r[^;&|]\s+/\s$',
        r'\bchmod\b[^;&|]\s+/\s$'
    ]
    for pat in high_risk_patterns:
        if re.search(pat, cmd):
            return True
    return False

Privacy risk.tool_code:
def pii_scanner(command: str):
    pii_patterns = [
        r'\b(grep|find)\s+.*(ssn|social|credit|card|phone|email)',
        r'\bdump\s+.*database',
        r'\bexport\s+.*user.*data'
    ]
    cmd = command.strip().lower()
    for pat in pii_patterns:
        if re.search(pat, cmd):
            return True
    return False

