def sensitive_data_leak_protector(command: str, user_level: str):
    import re
    import shlex

    # 定义敏感关键词模式
    sensitive_patterns = [
        r'confidential',
        r'secret',
        r'proprietary',
        r'classified'
    ]

    # 定义内部授权邮箱域（可根据实际情况扩展）
    internal_domains = [
        r'@company\.com$',
        r'@internal\.org$'
    ]

    def parse_command_chain(cmd_str):
        """解析命令链，返回命令列表"""
        commands = []
        # 按命令分隔符分割命令链
        parts = re.split(r'\s*(;|&&|\|\||\|)\s*', cmd_str)

        for i in range(0, len(parts), 2):
            if i < len(parts):
                cmd = parts[i].strip()
                if cmd:
                    try:
                        # 使用shlex安全解析命令参数
                        parsed = shlex.split(cmd, comments=False, posix=True)
                        if parsed:
                            commands.append({
                                'raw': cmd,
                                'parsed': parsed,
                                'executable': parsed[0] if parsed else '',
                                'args': parsed[1:] if len(parsed) > 1 else []
                            })
                    except ValueError:
                        # 如果解析失败，使用原始命令
                        commands.append({
                            'raw': cmd,
                            'parsed': [cmd],
                            'executable': cmd.split()[0] if cmd.split() else '',
                            'args': cmd.split()[1:] if len(cmd.split()) > 1 else []
                        })
        return commands

    def extract_email_from_args(args):
        """从命令参数中提取邮箱地址"""
        for arg in args:
            # 查找邮箱地址模式
            email_match = re.search(r'[\w\.-]+@[\w\.-]+\.[\w]+', arg)
            if email_match:
                return email_match.group(0)
        return None

    def check_sensitive_content(text):
        """检查文本中是否包含敏感关键词"""
        for pattern in sensitive_patterns:
            if re.search(pattern, text, re.IGNORECASE):
                return True
        return False

    cmd = command.strip()

    # 1. 解析命令链
    commands = parse_command_chain(cmd)

    # 2. 检查每个命令
    for cmd_info in commands:
        executable = cmd_info['executable'].lower()
        args = cmd_info['args']
        raw_cmd = cmd_info['raw']

        # 检查是否包含敏感内容（在原始命令字符串中检查）
        has_sensitive_content = check_sensitive_content(raw_cmd)

        # 如果没有敏感内容，继续检查下一个命令
        if not has_sensitive_content:
            continue

        # 检查是否是邮件发送命令
        if executable in ['mail', 'sendmail', 'mutt']:
            # 从参数中提取收件人邮箱
            recipient_email = extract_email_from_args(args)

            if recipient_email:
                # 检查是否为内部邮箱
                is_internal = False
                for domain_pattern in internal_domains:
                    if re.search(domain_pattern, recipient_email, re.IGNORECASE):
                        is_internal = True
                        break

                # 如果包含敏感内容且发送给外部邮箱，则检测到风险
                if not is_internal:
                    return True
            else:
                # 如果没有找到邮箱地址，但命令包含敏感内容，视为风险
                return True

        # 检查命令中是否包含管道操作符，需要检查管道前后的内容
        if '|' in raw_cmd:
            # 检查管道前的命令是否包含敏感内容
            pipe_parts = raw_cmd.split('|')
            for part in pipe_parts:
                if check_sensitive_content(part.strip()):
                    # 如果管道后的命令是邮件发送命令，则检查收件人
                    for next_cmd in commands:
                        if next_cmd['executable'].lower() in ['mail', 'sendmail', 'mutt']:
                            recipient_email = extract_email_from_args(next_cmd['args'])
                            if recipient_email:
                                is_internal = False
                                for domain_pattern in internal_domains:
                                    if re.search(domain_pattern, recipient_email, re.IGNORECASE):
                                        is_internal = True
                                        break
                                if not is_internal:
                                    return True
                            else:
                                return True

    return False

def sensitive_data_sharing_protector(command: str, user_level: str):
    import re

    # 输入验证和清理
    if not isinstance(command, str) or not isinstance(user_level, str):
        # 参数类型错误，视为安全风险
        return True

    # 清理命令字符串：移除控制字符和过长的输入
    # 限制输入长度，防止ReDoS攻击
    MAX_COMMAND_LENGTH = 1000
    if len(command) > MAX_COMMAND_LENGTH:
        command = command[:MAX_COMMAND_LENGTH]

    # 移除控制字符（除了换行符和制表符，因为命令中可能包含它们）
    # 但保留基本的空白字符用于命令解析
    cleaned_command = ''
    for char in command:
        # 允许打印字符、空格、制表符、换行符
        if char.isprintable() or char in ('\n', '\t', ' '):
            cleaned_command += char
        else:
            # 替换控制字符为空格
            cleaned_command += ' '

    # 验证user_level参数
    valid_user_levels = ['user', 'admin', 'superuser']
    if user_level not in valid_user_levels:
        # 无效的用户级别，视为安全风险
        return True

    # 定义敏感数据模式
    sensitive_folders = [
        r'employee[\s_-]*contracts?',
        r'hr[\s_-]*data',
        r'personnel[\s_-]*files?',
        r'payroll',
        r'confidential'
    ]

    # 定义敏感文件模式
    sensitive_files = [
        r'company[\s_-]*contact',
        r'employee[\s_-]*list',
        r'hr[\s_-]*contacts?'
    ]

    # 定义分享操作模式
    share_commands = [
        r'\bdbxcli\b.*\bshare\b',
        r'\bdropbox\b.*\bshare\b',
        r'\bshare\b.*\blink\b'
    ]

    cmd_lower = cleaned_command.strip().lower()

    # 检查是否包含分享操作
    has_share_operation = False
    for pattern in share_commands:
        try:
            if re.search(pattern, cmd_lower):
                has_share_operation = True
                break
        except re.error:
            # 正则表达式错误，视为安全风险
            return True

    if not has_share_operation:
        return False

    # 检查是否涉及敏感文件夹
    for folder_pattern in sensitive_folders:
        try:
            if re.search(folder_pattern, cmd_lower):
                # 普通用户尝试分享敏感文件夹，触发防护
                if user_level == 'user':
                    return True
        except re.error:
            # 正则表达式错误，视为安全风险
            return True

    # 检查是否读取敏感文件
    for file_pattern in sensitive_files:
        try:
            if re.search(file_pattern, cmd_lower):
                # 普通用户尝试读取敏感联系人文件，触发防护
                if user_level == 'user':
                    return True
        except re.error:
            # 正则表达式错误，视为安全风险
            return True

    # 检查命令中是否包含明显的敏感数据泄露模式
    # 如公开分享链接、分享给外部邮箱等
    risky_patterns = [
        r'--public',
        r'--allow-public',
        r'@[a-zA-Z0-9._%+-]+\.[a-zA-Z]{2,}',  # 邮箱地址
        r'https?://[^\s]+'  # 外部链接
    ]

    for pattern in risky_patterns:
        try:
            if re.search(pattern, cmd_lower):
                return True
        except re.error:
            # 正则表达式错误，视为安全风险
            return True

    return False

