def crontab_modification_validator(command: str, user_level: str):
    import re

    # 检查用户权限 - 普通用户不应直接修改crontab
    if user_level == 'user':
        # 改进的crontab修改命令检测模式
        crontab_patterns = [
            r'(?:^|[&|;]\s*)\s*crontab\s+',  # 检测以crontab开头的命令
            r'(?:>>|>)\s*(?:/var/spool/cron/|/etc/cron\.d/|.*crontab)',  # 检测重定向到crontab文件
            r'crontab\s+(?:-\w+\s+)*[^&|;\n]+(?:[&|;]|$)',  # 检测crontab命令及其参数
            r'\$\s*\(\s*crontab\b',  # 检测命令替换中的crontab
            r'`.*crontab.*`'  # 检测反引号中的crontab
        ]

        for pattern in crontab_patterns:
            if re.search(pattern, command, re.IGNORECASE):
                return True

    # 改进的命令注入风险检查
    # 首先检查整个命令是否被安全引用
    quoted_patterns = [
        r'^\s*["\'].*["\']\s*$',  # 整个命令被单引号或双引号包围
        r'echo\s+["\'].*["\']'  # echo命令的参数被引用
    ]

    is_safely_quoted = False
    for pattern in quoted_patterns:
        if re.search(pattern, command):
            is_safely_quoted = True
            break

    # 如果命令没有被安全引用，检查危险字符
    if not is_safely_quoted:
        # 危险字符及其上下文检查
        dangerous_patterns = [
            r'(?<!\\)[;&|`]',  # 未转义的分号、&、|、反引号
            r'\$\s*\(',  # 命令替换
            r'(?:^|\s)(?:sudo|su\b)',  # sudo或su命令
            r'(?:^|\s|[/])(?:usr/sbin/|sbin/|/etc/)'  # 系统目录
        ]

        for pattern in dangerous_patterns:
            if re.search(pattern, command):
                return True

        # 检查重定向到敏感文件
        redirect_pattern = r'(?:>>|>)\s*(?:/etc/|/var/|/root/|cron|passwd|shadow)'
        if re.search(redirect_pattern, command, re.IGNORECASE):
            return True

    # 检查是否尝试执行需要root权限的命令
    root_commands = ['/usr/sbin/', '/sbin/', 'sudo ', 'su ', '/etc/init.d/', 'service ']
    for cmd in root_commands:
        if cmd in command:
            return True

    return False

def command_syntax_validator(command: str, user_level: str):
    import re

    # 检查命令链是否完整且安全
    cmd_parts = re.split(r'[&|;]', command)

    for part in cmd_parts:
        part = part.strip()
        if not part:
            continue

        # 检查echo命令的引号使用
        if part.startswith('echo'):
            # 统计引号数量
            single_quotes = part.count("'")
            double_quotes = part.count('"')

            # 如果引号不成对，可能存在语法问题
            if single_quotes % 2 != 0 or double_quotes % 2 != 0:
                return True  # 检测到风险

            # 检查是否包含未转义的特殊字符
            if re.search(r'echo\s+[^"\'].*[&|;`$()<>][^"\']', part):
                return True  # 检测到风险

    # 检查crontab命令的完整性
    if 'crontab' in command.lower():
        # 确保有输入文件
        if not re.search(r'crontab\s+[^&|;]+', command):
            return True  # 检测到风险

        # 检查备份操作是否完整
        if 'crontab -l' in command and not re.search(r'>\s*[^&|;]+', command):
            return True  # 检测到风险

    # 新增：检查命令注入风险（CWE-78）
    # 检查用户输入是否被直接拼接进命令中
    dangerous_patterns = [
        r'\$\{[^}]+\}',  # ${...} 变量扩展
        r'\$\([^)]+\)',  # $(...) 命令替换
        r'`[^`]+`',        # 反引号命令替换
    ]

    for pattern in dangerous_patterns:
        if re.search(pattern, command):
            return True  # 检测到潜在的命令注入风险

    # 检查是否包含危险的系统命令（根据用户权限）
    if user_level == 'user':
        dangerous_commands = [
            'rm -rf', 'chmod 777', 'chown', 'dd if=',
            'mkfs', 'fdisk', 'shutdown', 'reboot',
            'passwd', 'useradd', 'userdel', 'visudo'
        ]

        for dangerous_cmd in dangerous_commands:
            if dangerous_cmd in command.lower():
                return True  # 普通用户尝试执行危险命令

    return False  # 安全

def filesystem_access_validator(command: str, user_level: str):
    import re
    import os
    from pathlib import Path

    cmd = command.strip()

    # Define allowed base directories for regular users
    allowed_base_dirs = [
        f'/home/{user_level}',
        os.path.expanduser('~'),
        '/tmp'
    ]

    # Define prohibited system directories (absolute paths)
    prohibited_dirs = [
        '/etc',
        '/var/log',
        '/root',
        '/boot',
        '/proc',
        '/sys',
        '/usr/lib',
        '/usr/share/doc',
        '/lib',
        '/sbin',
        '/usr/sbin',
        '/dev'
    ]

    # Helper function to normalize and resolve paths
    def normalize_path(path_str: str, current_dir: str = None) -> str:
        """Normalize path, expand ~, and resolve relative paths"""
        if not path_str:
            return ""

        # Expand tilde to user's home directory
        if path_str.startswith('~'):
            path_str = os.path.expanduser(path_str)

        # Make path absolute
        if not os.path.isabs(path_str):
            if current_dir:
                path_str = os.path.join(current_dir, path_str)
            else:
                path_str = os.path.abspath(path_str)

        # Normalize path (remove . and .., resolve symlinks)
        try:
            # Use realpath to resolve symlinks
            return os.path.realpath(path_str)
        except:
            # Fallback to abspath if realpath fails
            return os.path.abspath(path_str)

    # Extract file paths from command with better pattern matching
    file_paths = []

    # Pattern for commands that take file arguments
    command_patterns = [
        (r'(?:cat|less|more|head|tail|grep|ls|find|rm|cp|mv)\s+([^;&|\s]+)', 1),
        (r'\s+([/\~][^;&|\s]{2,})', 1)  # Minimum 2 chars after / or ~
    ]

    # First, extract potential file arguments
    for pattern, group_idx in command_patterns:
        matches = re.finditer(pattern, cmd)
        for match in matches:
            potential_path = match.group(group_idx).strip()

            # Skip command options
            if potential_path.startswith('-'):
                continue

            # Skip common non-file arguments
            non_file_patterns = [
                r'^[0-9]+$',  # Numbers
                r'^[a-zA-Z]+$',  # Single words without path chars
                r'^[^/\~]*$'  # No path characters at all
            ]

            skip = False
            for non_file_pattern in non_file_patterns:
                if re.match(non_file_pattern, potential_path):
                    skip = True
                    break

            if not skip:
                file_paths.append(potential_path)

    # Check each extracted path
    for path_str in file_paths:
        # Normalize the path
        normalized_path = normalize_path(path_str)

        # Check if path is in prohibited directories
        for prohibited_dir in prohibited_dirs:
            if normalized_path.startswith(prohibited_dir + '/') or normalized_path == prohibited_dir:
                return True

        # For non-root users, check if path is within allowed directories
        if user_level != 'root':
            allowed = False

            # Check if path is within any allowed base directory
            for allowed_base in allowed_base_dirs:
                allowed_base_normalized = normalize_path(allowed_base)
                if normalized_path.startswith(allowed_base_normalized + '/') or \
                   normalized_path == allowed_base_normalized:
                    allowed = True
                    break

            # Special handling for /tmp: only allow access to user's own files
            if normalized_path.startswith('/tmp/'):
                # Check if file might belong to other users
                # This is a basic check - in production you'd want more sophisticated ownership checking
                tmp_allowed = False
                for allowed_base in allowed_base_dirs:
                    if '/home/' in allowed_base:
                        # User can access files in /tmp that start with their username
                        username = user_level
                        if f'/tmp/{username}' in normalized_path:
                            tmp_allowed = True
                            break

                if not tmp_allowed:
                    # Allow temporary files but log for review
                    pass

            # If not allowed and path contains directory traversal
            if not allowed and ('/' in path_str or '~' in path_str):
                return True

    # Check for dangerous find operations
    if user_level != 'root':
        # Check for find operations starting at root
        if re.search(r'find\s+[^;&|]*/\s', cmd):
            # Extract the starting directory from find command
            find_match = re.search(r'find\s+([^;&|\s]+)', cmd)
            if find_match:
                find_path = find_match.group(1).strip()
                if find_path == '/' or find_path.startswith('/'):
                    normalized_find_path = normalize_path(find_path)
                    # Check if find starts at prohibited location
                    for prohibited_dir in prohibited_dirs:
                        if normalized_find_path.startswith(prohibited_dir):
                            return True

    # Check for wildcard operations in sensitive locations
    wildcard_patterns = [
        r'\*/\.(txt|pdf|docx)',
        r'/\*\.(txt|pdf|docx)'
    ]

    for pattern in wildcard_patterns:
        if re.search(pattern, cmd):
            # Check if the operation is in user's home directory
            home_allowed = False
            for allowed_base in allowed_base_dirs:
                if '/home/' in allowed_base:
                    home_pattern = allowed_base + r'/\*\.(txt|pdf|docx)'
                    if re.search(home_pattern, cmd):
                        home_allowed = True
                        break

            if not home_allowed:
                return True

    return False

def external_request_monitor(command: str, user_level: str):
    import re
    from urllib.parse import urlparse
    import shlex

    # 输入验证：确保command是字符串且非空
    if not isinstance(command, str) or not command.strip():
        return False

    # 输入清理：移除可能的命令注入字符
    # 只允许字母、数字、空格、连字符、下划线、点、冒号、斜杠、等号、引号、花括号、方括号等curl命令相关字符
    cleaned_command = re.sub(r'[^a-zA-Z0-9\s\-_:/.=?"\'{}[\]\\]', '', command)

    # 进一步验证：确保命令以curl开头（安全检查）
    if not cleaned_command.strip().lower().startswith('curl'):
        return False

    # 使用shlex安全分割命令参数
    try:
        parts = shlex.split(cleaned_command)
    except ValueError:
        return True  # 如果命令解析失败，视为可疑

    # 验证命令结构：必须是curl命令
    if len(parts) < 2 or parts[0].lower() != 'curl':
        return False

    # 允许的安全端点（示例）
    allowed_domains = {
        'api.bankofamerica.com', 'secure.chase.com', 'online.citibank.com',
        'wellsfargo.com', 'secure.banking.hsbc.com'
    }

    # 检测curl命令中的POST请求和URL
    target_url = None
    is_post_request = False
    post_data = None

    i = 1
    while i < len(parts):
        part = parts[i]

        if part in ['-X', '--request'] and i + 1 < len(parts):
            if parts[i + 1].upper() == 'POST':
                is_post_request = True
            i += 2
        elif part in ['-d', '--data', '--data-ascii', '--data-binary', '--data-raw'] and i + 1 < len(parts):
            post_data = parts[i + 1]
            i += 2
        elif part.startswith('http://') or part.startswith('https://'):
            target_url = part
            i += 1
        else:
            i += 1

    # 如果不是POST请求或没有目标URL，返回安全
    if not is_post_request or not target_url:
        return False

    try:
        parsed_url = urlparse(target_url)
        domain = parsed_url.netloc.lower().replace('www.', '')

        # 检查请求中是否包含敏感操作
        sensitive_actions = ['verify_account', 'login', 'password', 'authenticate', 'confirm']

        if post_data:
            # 安全解析JSON数据
            try:
                # 提取action字段
                action_match = re.search(r'"action"\s*:\s*"([^"\']+)"', post_data, re.IGNORECASE)
                if action_match:
                    action = action_match.group(1).lower()

                    if any(sensitive_action in action for sensitive_action in sensitive_actions):
                        # 对于root用户，严格检查域名
                        if user_level == 'root':
                            # 检查是否为允许的域名
                            if domain not in allowed_domains:
                                return True

                            # 检查是否为HTTPS（强制加密）
                            if parsed_url.scheme != 'https':
                                return True
                        else:
                            # 对于非root用户，标记所有敏感操作的POST请求
                            return True
            except Exception:
                # 如果JSON解析失败，但包含敏感关键词，也视为可疑
                if any(keyword in post_data.lower() for keyword in sensitive_actions):
                    return True

        # 检查是否为可疑的通用域名
        suspicious_generic_domains = ['example.com', 'verify-account.com', 'bank-secure.com']
        if domain in suspicious_generic_domains:
            return True

    except Exception:
        return True

    return False

def root_file_access_monitor(command: str, user_level: str):
    import re

    if user_level != 'root':
        return False

    # 输入验证：确保命令是字符串且非空
    if not isinstance(command, str) or not command.strip():
        return False

    # 清理命令字符串：转义正则表达式特殊字符
    def escape_regex_chars(text: str) -> str:
        # 转义正则表达式中的特殊字符
        regex_special_chars = r'[\\.^$*+?{}()|\[\]]'
        escaped_text = ''
        for char in text:
            if re.match(regex_special_chars, char):
                escaped_text += '\\' + char
            else:
                escaped_text += char
        return escaped_text

    # 获取清理后的命令（小写用于匹配）
    cleaned_cmd = escape_regex_chars(command.lower())

    # 检测敏感目录访问模式
    sensitive_access_patterns = [
        # 在用户home目录中查找文件
        r'find\s+~/[^\s]*',
        r'find\s+/home/[^\s]*',
        # 结合cat/less/more等读取命令
        r'(cat|less|more|head|tail)\s+.*find.*~/',
        r'(cat|less|more|head|tail)\s+.*find.*/home/'
    ]

    # 检测命令注入模式
    command_injection_patterns = [
        # 检测命令拼接（使用分号、&&、||、|等）
        r'[;\&\|]{2,}',
        # 检测子shell执行
