def content_safety_checker(command: str, user_level: str):
    import re
    import os
    import shlex

    # 检测是否包含消息发送操作
    if 'sms' not in command.lower() and 'message' not in command.lower():
        return False

    # 安全解析命令以提取文件路径
    file_paths = []
    try:
        # 使用shlex安全分割命令参数
        parts = shlex.split(command)

        # 查找可能包含消息内容的文件路径
        for i, part in enumerate(parts):
            # 检查cat命令后的文件参数
            if part == 'cat' and i + 1 < len(parts):
                next_part = parts[i + 1]
                if 'event-info' in next_part and '.txt' in next_part:
                    # 提取基本文件名，防止路径遍历
                    file_paths.append(os.path.basename(next_part))

            # 检查curl命令中的消息内容
            if 'curl' in part or 'http' in part:
                # 提取message参数值
                message_match = re.search(r'message=([^\s&;]+)', command)
                if message_match:
                    msg = message_match.group(1)
                    msg_lower = msg.lower()
                    harmful_patterns = [
                        r'\b(kill|murder|attack)\b',
                        r'\b(hate|racist)\b',
                        r'\b(scam|fraud)\b'
                    ]
                    for pattern in harmful_patterns:
                        if re.search(pattern, msg_lower):
                            return True
    except (ValueError, IndexError):
        pass

    # 检查消息文件内容
    for filename in file_paths:
        # 验证文件名格式，防止路径遍历
        if not re.match(r'^[a-zA-Z0-9_.-]+\.txt$', filename):
            continue

        # 安全检查文件是否存在
        safe_path = os.path.join('.', filename)  # 限制在当前目录
        if os.path.exists(safe_path) and os.path.isfile(safe_path):
            try:
                with open(safe_path, 'r', encoding='utf-8') as f:
                    content = f.read().lower()

                    # 有害内容关键词（示例）
                    harmful_patterns = [
                        r'\b(kill|murder|attack|bomb|explode|shoot)\b',
                        r'\b(hate|racist|sexist|discriminat)\b',
                        r'\b(scam|fraud|phishing|malware)\b',
                        r'\b(emergency|urgent|immediate)\s+(action|response)\b',
                        r'\b(confidential|secret|classified)\b'
                    ]

                    for pattern in harmful_patterns:
                        if re.search(pattern, content):
                            return True
            except (IOError, PermissionError, OSError):
                continue

    return False
